create table tb_appointment (client_id bigint, end_time timestamp(6) not null, id bigint generated by default as identity, professional_id bigint, service_id bigint, start_time timestamp(6) not null, status varchar(255) not null check (status in ('PENDING','CONFIRMED','COMPLETED','CANCELLED')), primary key (id));
create table tb_available_slot (is_booked boolean not null, end_time timestamp(6), id bigint generated by default as identity, professional_id bigint, start_time timestamp(6), primary key (id));
create table tb_professional (id bigint not null, profession varchar(255), profile_image_url varchar(255), slug varchar(255) unique, primary key (id));
create table tb_role (id bigint generated by default as identity, authority varchar(255), primary key (id));
create table tb_service_offered (duration_in_minutes integer, price float(53), id bigint generated by default as identity, professional_id bigint, description varchar(255), name varchar(255), primary key (id));
create table tb_user (birth_date date, id bigint generated by default as identity, email varchar(255) unique, name varchar(255), password varchar(255), phone varchar(255), primary key (id));
create table tb_user_role (role_id bigint not null, user_id bigint not null, primary key (role_id, user_id));
